using RecommendationEngine.Interfaces;using RecommendationEngine.Models;namespace RecommendationEngine.Services{    public class AuthService : IAuthService    {        private readonly IUserRepository _userRepository;        public AuthService(IUserRepository userRepository)        {            _userRepository = userRepository;        }        public bool Authenticate(string employeeId, string name, out string role)        {            role = string.Empty;            var user = _userRepository.FindByEmployeeIdAndName(employeeId, name);            if (user != null)            {                role = user.Role;                return true;            }            return false;        }    }}